import React, { useEffect, useRef, useState } from 'react';

import Web3Modal from "web3modal";
import { ethers, Contract, providers, Signer } from 'ethers';
import { AUCTION_ESCROW_CONTRACT_ADDRESS, AUCTION_ESCROW_ABI } from './constant';


export default function Auction() {
    const [clientAddress, setClientAddress] = useState();
    const [everyAuction, setEveryAuction] = useState([]);
    const [auctionId, setAuctionId] = useState();
    const [msp, setMsp] = useState(0);




    const web3ModalRef = useRef();

    const [walletConnected, setWalletConnected] = useState(false);
    const getProviderOrSigner = async (needSigner = false) => {
        const provider = await web3ModalRef.current.connect();

        const web3Provider = new providers.Web3Provider(provider);
        // console.log((await userAddress).toLowerCase())
        const signerForUserAddress = await web3Provider.getSigner();
        const clientAddress = await signerForUserAddress.getAddress();
        setClientAddress(clientAddress);
        const { chainId } = await web3Provider.getNetwork();
        if (chainId !== 5) {
            window.alert("Please switch to the Goerli network!");
            throw new Error("Please switch to the Goerli network");
        }

        if (needSigner) {
            const signer = web3Provider.getSigner();
            return signer;
        }
        return web3Provider;
    }
    // console.log('clientAddress:', clientAddress,); 
    const connectWallet = async () => {
        try {
            await getProviderOrSigner();
            setWalletConnected(true);
        } catch (error) {
            console.log(error);

        }
    }

    // Create Auction --------
    const createAuction = async () => {
        // Validate inputs
        if (auctionId == null || clientAddress == null || msp == null) {
            alert('Please enter all required fields.');
            return;
        }

        const signer = await getProviderOrSigner(true);
        const escroContract = getAuctionContractInstance(signer);
        // Send the transaction to create the escrow agreement
        const tx = await escroContract.startAuction(auctionId, clientAddress, { value: ethers.utils.parseEther(msp) });
        console.log('tx====', tx);
        setLoading(true)
        await tx.wait();
        setAgreementId("");
        setFunds(0)
        // Update the state to reflect the new escrow agreement
        setFundsReleased(false);

        getNumOfAgreements();
        setLoading(false);
        alert('Escrow agreement created successfully.');
        alert('Funds deposited successfully.');
    }

    useEffect(() => {
        if (!walletConnected) {
            web3ModalRef.current = new Web3Modal({
                network: "goerli",
                providerOptions: {},
                disableInjectedProvider: false,
            });
            connectWallet().then(async () => {
                "wallet connected"
            })
        }
    }, []);

    const fetchAuctionById = async (id) => {
        console.log('erntered fetch by id', id);

        try {
            const provider = await getProviderOrSigner();
            const escroContract = getAuctionContractInstance(provider);
            let agreement = await escroContract.agreements(id);

            const agrmnt = new ParsedAgreement(id, agreement.client, agreement.serviceProvider,
                // agreement.completed,
                agreement.msp.toNumber(), agreement.fundsReleased)

            console.log(agrmnt, 'agreement by ID');
            return agrmnt;

        } catch (error) {
            console.log(error);
        };
    }


    const getAuctionContractInstance = (providerOrSigner) => {
        return new Contract(
            AUCTION_ESCROW_CONTRACT_ADDRESS,
            AUCTION_ESCROW_ABI,
            providerOrSigner
        );
    };
    return (
        <>
            <div>
                <h1>
                    Auction by Di
                </h1>
            </div>
            <div>Owner : {clientAddress}</div>
            <div>Highest Bid : </div>
            {/* 1)Create Auction */}
            <div>
                <lable> Name of Auction : </lable> <input type="text" />
            </div>
            <lable> MSP : </lable> <input type="number" />

            <div>
                {/* <lable> Add Bid : </lable> <input type="number" /> */}
                <div> <button>Create Auction</button></div>
            </div>

        </>

    )
}
